mapper.select:
select
  id, name, max_number, create_by, create_time, update_by, update_time, status, del_flag
from carousel


        and id = #{id}


        and name = #{name}


        and max_number = #{maxNumber}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and status = #{status}


        and del_flag = #{delFlag}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from carousel


        and id = #{id}


        and name = #{name}


        and max_number = #{maxNumber}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and status = #{status}


        and del_flag = #{delFlag}

select

from
    carousel


        AND id = #{params.id}


        AND name LIKE CONCAT('%', #{params.name}, '%')


        AND max_number = #{params.maxNumber}


        AND create_by = #{params.createBy}


        AND create_time = #{params.createTime}


        AND update_by = #{params.updateBy}


        AND update_time = #{params.updateTime}


        AND status = #{params.status}


        AND del_flag = #{params.delFlag}


limit
    #{start},#{size}

